","keccak256":"0xa73959e6ef0b693e4423a562e612370160b934a75e618361ddd8c9c4b8ddbaaf","license":"MIT"},"contracts/SacredBeast.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;
pragma experimental ABIEncoderV2;

import "base64-sol/base64.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";

contract SacredBeast is ERC721Enumerable, Ownable, ReentrancyGuard {
    using Address for address payable;
    IERC20 foodToken;

    address payable public seller;

    uint256 public MAX_SUPPLY = 10000;
    uint256 public PRICE_PER_UNIT = 10 ether;

    struct LockChoice {
        uint256 duration;
        uint256 multiplier;
        uint256 penalty;
    }

    struct LockedInfo {
        uint256 amount;
        uint256 unlockTime;
        LockChoice lockChoice;
    }

    struct BeastStat {
        uint8 evolForm;
        uint256 exp;
    }

    struct Balances {
        uint256 total;
        uint256 unlocked;
        uint256 locked;
        uint256 earned;
    }

    struct Reward {
        uint256 periodFinish;
        uint256 rewardRate;
        uint256 lastUpdateTime;
        uint256 rewardPerTokenStored;
    }

    // tokenId -> BeastStat
    mapping(uint256 => BeastStat) public nft;

    uint256 public totalERC20Supply;
    uint256 public lockedSupply;

    address[] public rewardTokens;
    mapping(address => Reward) public rewardData;

    // Duration that rewards are streamed over
    uint256 public constant rewardsDuration = 86400 * 7;
    // reward token -> distributor -> is approved to add rewards
    mapping(address => mapping(address => bool)) public rewardDistributors;

    // lock Choice
    LockChoice[] public lockChoices;

    // token Id -> reward token -> amount
    mapping(uint256 => mapping(address => uint256))
        public nftRewardPerTokenPaid;
    mapping(uint256 => mapping(address => uint256)) public rewards;

    // lock Data tokenId -> Info
    mapping(uint256 => LockedInfo[]) public nftLocks;
    mapping(uint256 => Balances) public balances;

    bool internal _paused;

    event Feed(
        uint256 tokenId,
        uint256 amount,
        uint256 unlockTime,
        LockChoice choice
    );
    event Reclaim(uint256 tokenId, uint256 amount);

    event RewardPaid(address claimer, address rewardToken, uint256 reward);

    /***********************************|
  |          Initialization           |
  |__________________________________*/

    // foodToken : EVM Token, seller: seller address
    constructor(IERC20 _foodToken, address payable _seller)
        ERC721("SACRED BEAST", "SCB")
    {
        foodToken = _foodToken;
        seller = _seller;
    }

    /***********************************|
  |             Modifiers             |
  |__________________________________*/

    modifier onlyExist(uint256 _tokenId) {
        require(_exists(_tokenId), "Token does not exist");
        _;
    }

    modifier updateReward(uint256 _tokenId) {
        address token = address(foodToken);
        uint256 balance;
        uint256 supply = lockedSupply;
        rewardData[token].rewardPerTokenStored = _rewardPerToken(token, supply);
        rewardData[token].lastUpdateTime = lastTimeRewardApplicable(token);
        require(
            _exists(_tokenId),
            "ERC721Metadata: URI query for nonexistent token"
        );
        // Special case, use the locked balances and supply for stakingReward rewards
        rewards[_tokenId][token] = _earned(
            _tokenId,
            token,
            balances[_tokenId].locked,
            supply
        );
        nftRewardPerTokenPaid[_tokenId][token] = rewardData[token]
            .rewardPerTokenStored;
        balance = balances[_tokenId].total;

        supply = totalERC20Supply;
        for (uint256 i = 1; i < rewardTokens.length; i++) {
            token = rewardTokens[i];
            rewardData[token].rewardPerTokenStored = _rewardPerToken(
                token,
                supply
            );
            rewardData[token].lastUpdateTime = lastTimeRewardApplicable(token);

            rewards[_tokenId][token] = _earned(
                _tokenId,
                token,
                balance,
                supply
            );
            nftRewardPerTokenPaid[_tokenId][token] = rewardData[token]
                .rewardPerTokenStored;
        }
        _;
    }

    /***********************************|
  |     ADMIN CONFIGURATION              |
  |__________________________________*/

    // Add a new reward token to be distributed to stakers
    function AddlockChoice(
        uint256 _duration,
        uint256 _multiplier,
        uint256 _penalty
    ) public onlyOwner {
        require(_penalty <= 1, "Penalty cannot exceed 1");

        lockChoices.push(
            LockChoice({
                duration: _duration,
                multiplier: _multiplier,
                penalty: _penalty
            })
        );
    }

    // Add a new reward token to be distributed to stakers
    function SetLockChoice(
        uint256 _lockIndex,
        uint256 _duration,
        uint256 _multiplier,
        uint256 _penalty
    ) public onlyOwner {
        require(_penalty <= 1, "Penalty cannot exceed 1");
        LockChoice memory lockChoice = lockChoices[_lockIndex];
        lockChoice.duration = _duration;
        lockChoice.multiplier = _multiplier;
        lockChoice.penalty = _penalty;
    }

    // Add a new reward token to be distributed to stakers
    function addReward(address _rewardsToken, address _distributor)
        public
        onlyOwner
    {
        require(rewardData[_rewardsToken].lastUpdateTime == 0);
        rewardTokens.push(_rewardsToken);
        rewardData[_rewardsToken].lastUpdateTime = block.timestamp;
        rewardData[_rewardsToken].periodFinish = block.timestamp;
        rewardDistributors[_rewardsToken][_distributor] = true;
    }

    // Modify approval for an address to call notifyRewardAmount
    function approveRewardDistributor(
        address _rewardsToken,
        address _distributor,
        bool _approved
    ) external onlyOwner {
        require(rewardData[_rewardsToken].lastUpdateTime > 0);
        rewardDistributors[_rewardsToken][_distributor] = _approved;
    }

    /***********************************|
  |         NFT Functions               |
  |__________________________________*/

    /***********************************|
  |              Public               |
  |__________________________________*/

    // Mint new NFT
    function mintBeast(uint256 _numberOfTokens) public {
        require(
            totalSupply() + _numberOfTokens <= MAX_SUPPLY,
            "Purchase would exceed max supply"
        );
        uint256 payAmount = PRICE_PER_UNIT * _numberOfTokens;
        require(
            foodToken.transferFrom(msg.sender, seller, payAmount),
            "Wrong pay amount"
        );

        for (uint256 i = 0; i < _numberOfTokens; i++) {
            uint256 mintIndex = totalSupply();
            if (totalSupply() < MAX_SUPPLY) {
                _safeMint(msg.sender, mintIndex);
                nft[mintIndex] = BeastStat({evolForm: 1, exp: 0});
            }
        }
    }
    //Get nftlocks.length
    function feededlists(uint256 _tokenId)
            public
            view
            returns (uint256)
        {
            return nftLocks[_tokenId].length;
                
        }
    //get unlock amount
    function getunlockamount(uint256 _tokenId)
            public
            view
            returns (uint256)
        {
        LockedInfo[] storage locks = nftLocks[_tokenId];
        Balances storage bal = balances[_tokenId];
        uint256 amount;
        uint256 length = locks.length;
        if (locks[length - 1].unlockTime <= block.timestamp) {
            amount = bal.locked;
        } else {
            for (uint256 i = 0; i < length; i++) {
                if (locks[i].unlockTime > block.timestamp) break;
                amount = amount + locks[i].amount;
            }
        }
        return amount;        
        }
    // Get Token URI
    function tokenURI(uint256 _tokenId)
        public
        view
        virtual
        override
        returns (string memory)
    {
        require(
            _exists(_tokenId),
            "ERC721Metadata: URI query for nonexistent token"
        );

        string memory baseURI = _baseURI();
        string memory name = string(
            abi.encodePacked("SACRED BEAST #", toString(_tokenId))
        );
        string memory exp = toString(nft[_tokenId].exp);
        string memory evolForm = toString(nft[_tokenId].evolForm);
        string memory image = bytes(baseURI).length > 0
            ? string(
                abi.encodePacked(
                    baseURI,
                    "/",
                    toString(nft[_tokenId].evolForm),
                    ".png"
                )
            )
            : "";

        return
            string(
                abi.encodePacked(
                    "data:application/json;base64,",
                    Base64.encode(
                        bytes(
                            abi.encodePacked(
                                '{"name":"',
                                name,
                                '", "description":"Sacred Beast is the mysthical beast of EVERMOON world. It can consume EVERMOON Token for exp and get stronger.", "exp":"',
                                exp,
                                '", "evolForm":"',
                                evolForm,
                                '", "image": "',
                                image,
                                '"}'
                            )
                        )
                    )
                )
            );
    }

    // Feed the beast by consume Evermoon (ERC20) token, Beast's exp will be increased by tokenAmount * lockTime * multiplier
    function feed(
        uint256 _tokenId,
        uint256 _amount,
        uint256 _lockIndex
    ) public onlyExist(_tokenId) {
        require(foodToken.transferFrom(msg.sender, address(this), _amount));
        totalERC20Supply = totalERC20Supply + _amount;
        Balances storage bal = balances[_tokenId];
        bal.total = bal.total + _amount;
        bal.locked = bal.locked + _amount;
        LockChoice memory lockChoice = lockChoices[_lockIndex];
        uint256 gainExp = _calConsumeEXP(_amount, lockChoice);
        nft[_tokenId].exp += gainExp;

        uint256 unlockTime = block.timestamp + lockChoice.duration;
        uint256 idx = nftLocks[_tokenId].length;
        if (idx == 0 || nftLocks[_tokenId][idx - 1].unlockTime < unlockTime) {
            nftLocks[_tokenId].push(
                LockedInfo({
                    amount: _amount,
                    unlockTime: unlockTime,
                    lockChoice: lockChoice
                })
            );
        } else {
            nftLocks[_tokenId][idx - 1].amount =
                nftLocks[_tokenId][idx - 1].amount +
                _amount;
        }

        emit Feed(_tokenId, _amount, unlockTime, lockChoice);
    }

    function _rewardPerToken(address _rewardsToken, uint256 _supply)
        internal
        view
        returns (uint256)
    {
        if (_supply == 0) {
            return rewardData[_rewardsToken].rewardPerTokenStored;
        }
        return
            rewardData[_rewardsToken].rewardPerTokenStored +
            ((((lastTimeRewardApplicable(_rewardsToken) -
                rewardData[_rewardsToken].lastUpdateTime) *
                rewardData[_rewardsToken].rewardRate) * (1e18)) / _supply);
    }

    function _earned(
        uint256 _tokenId,
        address _rewardsToken,
        uint256 _balance,
        uint256 supply
    ) internal view returns (uint256) {
        return
            ((_balance *
                (_rewardPerToken(_rewardsToken, supply) -
                    nftRewardPerTokenPaid[_tokenId][_rewardsToken])) / 1e18) +
            (rewards[_tokenId][_rewardsToken]);
    }

    function lastTimeRewardApplicable(address _rewardsToken)
        public
        view
        returns (uint256)
    {
        return
            Math.min(block.timestamp, rewardData[_rewardsToken].periodFinish);
    }

    function rewardPerToken(address _rewardsToken)
        external
        view
        returns (uint256)
    {
        uint256 supply = _rewardsToken == address(foodToken)
            ? lockedSupply
            : totalERC20Supply;
        return _rewardPerToken(_rewardsToken, supply);
    }

    function getRewardForDuration(address _rewardsToken)
        external
        view
        returns (uint256)
    {
        return rewardData[_rewardsToken].rewardRate * rewardsDuration;
    }

    /***********************************|
  |     Only Token ID Owner             |
  |__________________________________*/

    // Withdraw all currently locked tokens where the unlock time has passed
    function reclaimExpiredLocks(uint256 _tokenId) public onlyExist(_tokenId) {
        require(ownerOf(_tokenId) == msg.sender, "Not Owner of NFT");
        LockedInfo[] storage locks = nftLocks[_tokenId];
        Balances storage bal = balances[_tokenId];
        uint256 amount;
        uint256 length = locks.length;
        if (locks[length - 1].unlockTime <= block.timestamp) {
            amount = bal.locked;
            delete nftLocks[_tokenId];
        } else {
            for (uint256 i = 0; i < length; i++) {
                if (locks[i].unlockTime > block.timestamp) break;
                amount = amount + locks[i].amount;
                delete locks[i];
            }
        }
        bal.locked = bal.locked - amount;
        bal.total = bal.total - amount;
        totalERC20Supply = totalERC20Supply - amount;
        lockedSupply = lockedSupply - amount;
        foodToken.transfer(msg.sender, amount);
        // Emit event
        emit Reclaim(_tokenId, amount);
    }

    // Claim all pending staking rewards
    function getReward(uint256 _tokenId)
        public
        nonReentrant
        updateReward(_tokenId)
    {
        for (uint256 i; i < rewardTokens.length; i++) {
            address _rewardsToken = rewardTokens[i];
            uint256 reward = rewards[_tokenId][_rewardsToken];
            if (reward > 0) {
                rewards[_tokenId][_rewardsToken] = 0;
                IERC20(_rewardsToken).transfer(msg.sender, reward);
                emit RewardPaid(msg.sender, _rewardsToken, reward);
            }
        }
    }

    // Evole form of NFT -> If EXP gathered > (BaseExp: 10 ** 6) * (5 ** EvolForm) * (Decimal 10 ** 18)
    function evolutionNFT(uint256 _tokenId) public {
        require(ownerOf(_tokenId) == msg.sender);
        // if true evolForm += 1
        require(
            nft[_tokenId].exp >
                (10**6) * (5**(nft[_tokenId].evolForm)) * (10**18)
        );
        nft[_tokenId].evolForm += 1;
    }

